import { Component, Input } from '@angular/core';
import { Player } from '../common/enums/player.enum';
import { ISquare } from '../common/interfaces/square.interface';

@Component({
  selector: 'app-square',
  templateUrl: './square.component.html',
  styleUrls: ['./square.component.scss']
})
export class SquareComponent {
  @Input() value!: ISquare;

  Player = Player;

  constructor() { }

}
<button type="button" aria-label="Current square value" [disabled]="value.disabled" [ngClass]="{
    'player' : value.value,
    'player-x': value.value === Player.X,
    'player-o': value.value === Player.O,
    'btn': true
  }">
  {{ value.value }}
</button>
